import { useState } from 'react';
import { format } from 'date-fns';

export function useAppData() {
    const [currentDate, setCurrentDate] = useState(new Date());

    // === Default Initial State ===

    // 1. Categories
    const [expenseCategories, setExpenseCategories] = useState([
        { id: 'food', label: '?앸퉬', icon: 'Utensils' },
        { id: 'transit', label: '援먰넻鍮?, icon: 'Train' },
        { id: 'cafe', label: '移댄럹', icon: 'Coffee' },
        { id: 'shopping', label: '?쇳븨', icon: 'ShoppingBag' },
    ]);

    const [incomeCategories, setIncomeCategories] = useState([
        { id: 'salary', label: '湲됱뿬', icon: 'Briefcase' },
        { id: 'allowance', label: '?⑸룉', icon: 'PieChart' },
        { id: 'bonus', label: '蹂대꼫??, icon: 'Target' },
    ]);

    const [scheduleCategories, setScheduleCategories] = useState([
        { id: 'work', label: '?낅Т', icon: 'Briefcase' },
        { id: 'meal', label: '?앹궗', icon: 'Utensils' },
        { id: 'meeting', label: '誘명똿', icon: 'Target' },
        { id: 'workout', label: '?대룞', icon: 'Calendar' },
        { id: 'study', label: '?숈뒿', icon: 'BookOpen' },
    ]);

    // Customizable Categories Handler
    const addCategory = (type, newCategory) => {
        if (type === 'expense') setExpenseCategories(p => [...p, newCategory]);
        else if (type === 'income') setIncomeCategories(p => [...p, newCategory]);
        else setScheduleCategories(p => [...p, newCategory]);
    };

    // 2. Accounts / Asset Balance
    const [accounts, setAccounts] = useState([
        { id: 'cash', name: '?꾧툑', default: true },
        { id: 'bank1', name: '援??????덇툑' },
        { id: 'bank2', name: '?좏븳????낆텧湲? }
    ]);
    const [initialBalances] = useState({ cash: 50000, bank1: 5000000, bank2: 1200000 });

    // 3. Transactions (Both Expenses & Incomes)
    const [transactions, setTransactions] = useState([
        { id: 1, type: 'expense', date: format(new Date(), 'yyyy-MM-dd'), time: '08:30 AM', title: '?ㅽ?踰낆뒪', amount: 4500, category: '移댄럹', memo: '異쒓렐湲?而ㅽ뱦', accountId: 'bank2' },
        { id: 2, type: 'income', date: format(new Date(), 'yyyy-MM-dd'), time: '09:00 AM', title: '?⑸룉', amount: 150000, category: '?⑸룉', memo: '', accountId: 'bank2' },
        { id: 3, type: 'expense', date: format(new Date(), 'yyyy-MM-dd'), time: '12:40 PM', title: '?먯떖 ?앹궗', amount: 12000, category: '?앸퉬', memo: '?덇퉴??, accountId: 'bank2' },
    ]);

    // 4. Schedules
    const [schedules, setSchedules] = useState([
        { id: 1, date: format(new Date(), 'yyyy-MM-dd'), time: '09:00 AM', category: '?낅Т', title: '? 誘명똿', completed: true, memo: '二쇨컙 ?낅Т 蹂닿퀬' },
    ]);

    // 5. Goals
    const [goals, setGoals] = useState([
        { id: 1, type: 'short', title: '?ㅻ뒛源뚯? ?댁빞 ?섎뒗 怨쇱젣', progress: 0, target: 100, deadline: '2026-03-01' },
        { id: 2, type: 'mid', title: '?대쾲 ?숆린 ?숈젏 4.0', progress: 20, target: 100, deadline: '2026-06-30' },
        { id: 3, type: 'long', title: '泥쒕쭔??紐⑥쑝湲??꾨줈?앺듃', progress: 45, target: 100, targetAmount: 10000000, currentAmount: 4500000, deadline: '2026-12-31' },
    ]);

    // Calculate actual balances
    const getCalculatedBalances = () => {
        let balances = { ...initialBalances };
        transactions.forEach(t => {
            if (!balances[t.accountId]) balances[t.accountId] = 0;
            if (t.type === 'income') balances[t.accountId] += t.amount;
            else if (t.type === 'expense') balances[t.accountId] -= t.amount;
        });
        return balances;
    };

    const totalAssets = Object.values(getCalculatedBalances()).reduce((a, b) => a + b, 0);

    // Computed Date strings
    const filterDateStr = format(currentDate, 'yyyy-MM-dd');

    return {
        currentDate, setCurrentDate, filterDateStr,
        expenseCategories, incomeCategories, scheduleCategories, addCategory,
        accounts, getCalculatedBalances, totalAssets,
        transactions, setTransactions,
        schedules, setSchedules,
        goals, setGoals
    };
}

import React, { useState } from 'react';
import { IconMap } from '../components/IconMap';
import { format, isSameDay, isSameWeek, isSameMonth, parseISO } from 'date-fns';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, ResponsiveContainer } from 'recharts';

export default function FinanceView({ transactions, setTransactions, getCalculatedBalances, accounts, currentDate }) {
    const { PieChart, Utensils, Coffee, Train, ShoppingBag, ChevronDown, Trash2, Wallet, TrendingDown } = IconMap;

    const [filterType, setFilterType] = useState('monthly'); // daily, weekly, monthly, all
    const [expandedId, setExpandedId] = useState(null);

    const filteredTransactions = transactions.filter(t => {
        if (filterType === 'all') return true;
        const tDate = parseISO(t.date);
        if (filterType === 'daily') return isSameDay(tDate, currentDate);
        if (filterType === 'weekly') return isSameWeek(tDate, currentDate);
        if (filterType === 'monthly') return isSameMonth(tDate, currentDate);
        return true;
    }).sort((a, b) => b.id - a.id);

    const incomeTotal = filteredTransactions.filter(t => t.type === 'income').reduce((acc, curr) => acc + curr.amount, 0);
    const expenseTotal = filteredTransactions.filter(t => t.type === 'expense').reduce((acc, curr) => acc + curr.amount, 0);

    const balances = getCalculatedBalances();
    const totalAssets = Object.values(balances).reduce((a, b) => a + b, 0);

    // Mock Asset Trend Data for chart (in reality this would be calculated per day)
    const chartData = [
        { name: 'Jan', asset: totalAssets * 0.8 },
        { name: 'Feb', asset: totalAssets * 0.9 },
        { name: 'Mar', asset: totalAssets },
    ];

    const deleteTransaction = (id) => {
        if (window.confirm('?댁뿭????젣?섏떆寃좎뒿?덇퉴?')) setTransactions(prev => prev.filter(t => t.id !== id));
    };

    return (
        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6 fade-in mb-8">
            {/* Left Col: Lists */}
            <div className="bg-white rounded-[1.5rem] p-6 shadow-sm border border-slate-100 min-h-[500px]">
                <header className="mb-6 border-b border-slate-100 pb-4 flex flex-col md:flex-row justify-between items-start md:items-center gap-4">
                    <h2 className="text-xl font-black flex items-center gap-2 text-slate-800">
                        <PieChart className="w-6 h-6 text-emerald-500" /> ?ъ젙 ?몃? ?댁뿭
                    </h2>
                    <div className="flex bg-slate-100 p-1 rounded-xl">
                        {['daily', 'weekly', 'monthly', 'all'].map(type => (
                            <button key={type} onClick={() => setFilterType(type)} className={`px-4 py-1.5 text-xs font-bold rounded-lg transition-colors ${filterType === type ? 'bg-white text-emerald-600 shadow-sm' : 'text-slate-500 hover:text-slate-700'}`}>
                                {type === 'daily' ? '?쇨컙' : type === 'weekly' ? '二쇨컙' : type === 'monthly' ? '?붽컙' : '?꾩껜'}
                            </button>
                        ))}
                    </div>
                </header>

                {filteredTransactions.length === 0 ? (
                    <div className="text-center py-20 text-slate-400">
                        <Wallet className="w-12 h-12 mx-auto mb-4 text-slate-200" />
                        <p className="font-bold">議곌굔??留욌뒗 ?댁뿭???놁뒿?덈떎.</p>
                    </div>
                ) : (
                    <div className="space-y-4">
                        {filteredTransactions.map(exp => (
                            <div key={exp.id} className="flex flex-col border border-slate-100 bg-white shadow-sm p-4 rounded-[1.25rem] hover:shadow-md transition-all">
                                <div className="flex justify-between items-center cursor-pointer select-none" onClick={() => setExpandedId(expandedId === exp.id ? null : exp.id)}>
                                    <div className="flex items-center gap-3">
                                        <div className={`w-10 h-10 rounded-full flex justify-center items-center font-black text-sm
                      ${exp.type === 'income' ? 'bg-blue-50 text-blue-500' : 'bg-rose-50 text-rose-500'}`}>
                                            {exp.type === 'income' ? '?섏엯' : '吏異?}
                                        </div>
                                        <div>
                                            <span className="block font-bold text-slate-800 text-sm md:text-base">{exp.title}</span>
                                            <span className="text-slate-400 font-medium text-[11px] mt-0.5">{exp.date} ??{exp.time} ??{exp.category}</span>
                                        </div>
                                    </div>
                                    <div className="flex items-center gap-3">
                                        <span className={`font-extrabold text-base md:text-lg ${exp.type === 'income' ? 'text-blue-500' : 'text-rose-500'}`}>
                                            {exp.type === 'income' ? '+' : '-'}{exp.amount.toLocaleString()}??r
                                        </span>
                                        <ChevronDown className={`w-4 h-4 text-slate-400 transition-transform ${expandedId === exp.id ? 'rotate-180' : ''}`} />
                                    </div>
                                </div>

                                <div className={`overflow-hidden transition-all duration-300 ease-in-out ${expandedId === exp.id ? 'max-h-40 opacity-100 mt-4 pt-4 border-t border-slate-100' : 'max-h-0 opacity-0'}`}>
                                    <div className="flex flex-col md:flex-row md:items-end justify-between gap-3 bg-slate-50 p-3 rounded-xl border border-slate-100">
                                        <div>
                                            <span className="text-xs font-bold text-indigo-500 bg-indigo-50 px-2 py-0.5 rounded-full inline-block mb-1">{accounts.find(a => a.id === exp.accountId)?.name}</span>
                                            <p className="text-sm text-slate-600 font-medium leading-relaxed">{exp.memo || '?몃? ?댁뿭???놁뒿?덈떎.'}</p>
                                        </div>
                                        <button onClick={() => deleteTransaction(exp.id)} className="text-[11px] flex items-center gap-1 font-bold text-rose-500 bg-white border border-rose-100 px-3 py-1.5 rounded-lg hover:bg-rose-50 transition-colors">
                                            <Trash2 className="w-3 h-3" /> ??젣
                                        </button>
                                    </div>
                                </div>
                            </div>
                        ))}
                    </div>
                )}
            </div>

            {/* Right Col: Stats & Bank Accounts */}
            <div className="flex flex-col gap-6">
                {/* Total Assets Overview */}
                <div className="bg-slate-800 rounded-[1.5rem] p-6 shadow-sm text-white relative overflow-hidden">
                    <div className="absolute -top-10 -right-10 w-40 h-40 bg-emerald-500/20 rounded-full blur-3xl"></div>
                    <h3 className="text-sm font-bold text-slate-400 flex items-center gap-2 mb-2">
                        <Wallet className="w-4 h-4 text-emerald-400" /> 珥??먯궛 (?덇툑/?꾧툑 ?ы븿)
                    </h3>
                    <div className="flex items-end gap-3 mb-6">
                        <span className="text-3xl font-black text-white">{totalAssets.toLocaleString()}??/span>
                    </div>

                    <div className="grid grid-cols-2 gap-4 border-t border-slate-700/50 pt-4">
                        <div><p className="text-xs text-slate-400 mb-1">珥??섏엯</p><p className="text-lg font-bold text-blue-400">+{incomeTotal.toLocaleString()}</p></div>
                        <div><p className="text-xs text-slate-400 mb-1">珥?吏異?/p><p className="text-lg font-bold text-rose-400">-{expenseTotal.toLocaleString()}</p></div>
                    </div>
                </div>

                {/* Bank Balances */}
                <div className="bg-white rounded-[1.5rem] p-6 shadow-sm border border-slate-100">
                    <h3 className="text-base font-bold text-slate-800 mb-4">?먯궛 怨꾩쥖 ?꾪솴</h3>
                    <div className="space-y-3">
                        {accounts.map(acc => (
                            <div key={acc.id} className="flex justify-between items-center bg-slate-50 p-3 rounded-xl">
                                <span className="text-sm font-bold text-slate-700">{acc.name}</span>
                                <span className="text-sm font-extrabold text-slate-800">{(balances[acc.id] || 0).toLocaleString()}??/span>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Chart */}
                <div className="bg-white rounded-[1.5rem] p-6 shadow-sm border border-slate-100 flex-1 min-h-[250px] flex flex-col">
                    <h3 className="text-base font-bold text-slate-800 mb-4">?먯궛 利앷? 異붿씠</h3>
                    <div className="flex-1 w-full relative">
                        <ResponsiveContainer width="100%" height="100%">
                            <LineChart data={chartData}>
                                <CartesianGrid strokeDasharray="3 3" vertical={false} stroke="#f1f5f9" />
                                <XAxis dataKey="name" axisLine={false} tickLine={false} tick={{ fontSize: 12, fill: '#94a3b8' }} dy={10} />
                                <Tooltip
                                    contentStyle={{ borderRadius: '12px', border: 'none', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }}
                                    formatter={(val) => [`${val.toLocaleString()}??, '?먯궛']}
                                />
                                <Line type="monotone" dataKey="asset" stroke="#10b981" strokeWidth={4} dot={{ r: 4, fill: '#10b981', strokeWidth: 2, stroke: '#fff' }} activeDot={{ r: 6 }} />
                            </LineChart>
                        </ResponsiveContainer>
                    </div>
                </div>
            </div>
        </div>
    );
}

import React, { useState } from 'react';
import { IconMap } from '../components/IconMap';

export default function GoalView({ goals, setGoals }) {
    const { Flag, Plus, Trash2, CheckCircle2 } = IconMap;

    // Categorize
    const shorts = goals.filter(g => g.type === 'short');
    const mids = goals.filter(g => g.type === 'mid');
    const longs = goals.filter(g => g.type === 'long');

    const deleteGoal = (id) => {
        if (window.confirm('??紐⑺몴瑜???젣?섏떆寃좎뒿?덇퉴?')) setGoals(prev => prev.filter(g => g.id !== id));
    };

    const updateProgress = (id, newProgress) => {
        setGoals(prev => prev.map(g => g.id === id ? { ...g, progress: Math.min(100, Math.max(0, newProgress)) } : g));
    };

    const GoalCard = ({ goal, colorFrom, colorTo, badgeText }) => (
        <div className="bg-white border border-slate-100 rounded-2xl p-5 shadow-sm hover:shadow-md transition-shadow flex flex-col justify-between">
            <div className="flex justify-between items-start mb-4">
                <div>
                    <span className={`text-[10px] uppercase font-black px-2 py-1 rounded-md mb-2 inline-block text-white bg-gradient-to-r ${colorFrom} ${colorTo}`}>{badgeText}</span>
                    <h3 className="text-lg font-black text-slate-800 line-clamp-2">{goal.title}</h3>
                    <p className="text-xs font-bold text-slate-400 mt-1">留덇컧: {goal.deadline}</p>
                </div>
                <button onClick={() => deleteGoal(goal.id)} className="text-slate-300 hover:text-rose-500 transition-colors p-1">
                    <Trash2 className="w-4 h-4" />
                </button>
            </div>

            <div>
                <div className="flex justify-between text-xs mb-1.5 items-end">
                    <span className="font-bold text-slate-500">?ъ꽦瑜? {goal.progress}%</span>
                    {goal.targetAmount && <span className="text-slate-700 font-bold">{goal.currentAmount?.toLocaleString()} / {goal.targetAmount?.toLocaleString()}??/span>}
                </div>
                <div className="h-3 w-full bg-slate-100 rounded-full overflow-hidden shadow-inner flex items-center group/slider relative">
                    <div className={`h-full bg-gradient-to-r ${colorFrom} ${colorTo} rounded-full transition-all duration-500`} style={{ width: `${goal.progress}%` }}></div>
                    {/* Progress Control - click to increase/decrease for demo */}
                    <div className="absolute inset-0 flex justify-between px-1 opacity-0 group-hover/slider:opacity-100 transition-opacity">
                        <button onClick={() => updateProgress(goal.id, goal.progress - 5)} className="text-[10px] font-bold text-slate-500 px-2 bg-white/80 rounded-sm">-</button>
                        <button onClick={() => updateProgress(goal.id, goal.progress + 5)} className="text-[10px] font-bold text-slate-500 px-2 bg-white/80 rounded-sm">+</button>
                    </div>
                </div>
            </div>
        </div>
    );

    return (
        <div className="bg-slate-50 min-h-[600px] fade-in mb-8">
            <header className="flex justify-between items-center mb-6 px-2">
                <h2 className="text-2xl font-black flex items-center gap-2 text-slate-800">
                    <Flag className="w-7 h-7 text-indigo-600" /> 醫낇빀 紐⑺몴 愿由?r
                </h2>
                <button className="bg-indigo-600 text-white hover:bg-indigo-700 px-4 py-2 rounded-xl text-sm font-bold transition-all shadow-md flex items-center gap-1.5">
                    <Plus className="w-4 h-4 stroke-[3]" /> ??紐⑺몴 ?묒꽦
                </button>
            </header>

            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                {/* Short Term */}
                <div className="flex flex-col gap-4">
                    <div className="bg-slate-200/50 px-4 py-2 rounded-xl flex items-center justify-between">
                        <h3 className="font-bold text-slate-700 text-sm">?④린 (怨쇱젣/?щ?由?</h3>
                        <span className="bg-white text-slate-500 text-[10px] px-2 py-0.5 rounded-full font-bold">{shorts.length}嫄?/span>
                    </div>
                    {shorts.map(g => <GoalCard key={g.id} goal={g} colorFrom="from-rose-400" colorTo="to-orange-400" badgeText="Short-term" />)}
                </div>

                {/* Mid Term */}
                <div className="flex flex-col gap-4">
                    <div className="bg-slate-200/50 px-4 py-2 rounded-xl flex items-center justify-between">
                        <h3 className="font-bold text-slate-700 text-sm">以묎린 (?숆린/遺꾧린)</h3>
                        <span className="bg-white text-slate-500 text-[10px] px-2 py-0.5 rounded-full font-bold">{mids.length}嫄?/span>
                    </div>
                    {mids.map(g => <GoalCard key={g.id} goal={g} colorFrom="from-blue-400" colorTo="to-indigo-500" badgeText="Mid-term" />)}
                </div>

                {/* Long Term */}
                <div className="flex flex-col gap-4">
                    <div className="bg-slate-200/50 px-4 py-2 rounded-xl flex items-center justify-between">
                        <h3 className="font-bold text-slate-700 text-sm">?κ린 (?곌컙/?몄깮)</h3>
                        <span className="bg-white text-slate-500 text-[10px] px-2 py-0.5 rounded-full font-bold">{longs.length}嫄?/span>
                    </div>
                    {longs.map(g => <GoalCard key={g.id} goal={g} colorFrom="from-purple-500" colorTo="to-fuchsia-500" badgeText="Long-term" />)}
                </div>
            </div>
        </div>
    );
}

import React, { useState } from 'react';
import { IconMap } from '../components/IconMap';
import { format, isSameDay, isSameWeek, isSameMonth, parseISO } from 'date-fns';

export default function ScheduleView({ schedules, setSchedules, currentDate }) {
    const { Calendar, CheckCircle2, Circle, ChevronDown, Check, Trash2 } = IconMap;

    const [filterType, setFilterType] = useState('daily'); // daily, weekly, monthly, all
    const [expandedId, setExpandedId] = useState(null);

    const filteredSchedules = schedules.filter(s => {
        if (filterType === 'all') return true;
        const sDate = parseISO(s.date);
        if (filterType === 'daily') return isSameDay(sDate, currentDate);
        if (filterType === 'weekly') return isSameWeek(sDate, currentDate);
        if (filterType === 'monthly') return isSameMonth(sDate, currentDate);
        return true;
    }).sort((a, b) => a.date.localeCompare(b.date) || a.time.localeCompare(b.time));

    const toggleSchedule = (e, id) => {
        e.stopPropagation();
        setSchedules(prev => prev.map(s => s.id === id ? { ...s, completed: !s.completed } : s));
    };
    const deleteSchedule = (id) => {
        if (window.confirm('?쇱젙????젣?섏떆寃좎뒿?덇퉴?')) {
            setSchedules(prev => prev.filter(s => s.id !== id));
        }
    };

    return (
        <div className="bg-white rounded-[1.5rem] p-6 shadow-sm border border-slate-100 min-h-[500px] fade-in mb-8">
            <header className="mb-6 border-b border-slate-100 pb-4 flex flex-col md:flex-row md:items-center justify-between gap-4">
                <h2 className="text-xl font-black flex items-center gap-2 text-slate-800">
                    <Calendar className="w-6 h-6 text-indigo-500" /> ?몃? 異붿쿇 ?쇱젙
                </h2>
                {/* Filters */}
                <div className="flex bg-slate-100 p-1 rounded-xl">
                    {['daily', 'weekly', 'monthly', 'all'].map(type => (
                        <button
                            key={type}
                            onClick={() => setFilterType(type)}
                            className={`px-4 py-1.5 text-xs font-bold rounded-lg transition-colors ${filterType === type ? 'bg-white text-indigo-600 shadow-sm' : 'text-slate-500 hover:text-slate-700'}`}
                        >
                            {type === 'daily' ? '?쇨컙' : type === 'weekly' ? '二쇨컙' : type === 'monthly' ? '?붽컙' : '?꾩껜'}
                        </button>
                    ))}
                </div>
            </header>

            {filteredSchedules.length === 0 ? (
                <div className="text-center py-20 text-slate-400">
                    <Calendar className="w-12 h-12 mx-auto mb-4 text-slate-200" />
                    <p className="font-bold">議곌굔??留욌뒗 ?쇱젙???놁뒿?덈떎.</p>
                </div>
            ) : (
                <div className="relative border-l-[3px] border-slate-100 ml-4 space-y-6">
                    {filteredSchedules.map((schedule) => (
                        <div key={schedule.id} className="relative pl-6 flex flex-col group">
                            <div className="flex items-start justify-between">
                                {/* Node */}
                                <div className={`absolute -left-[12px] top-1 rounded-full p-0.5 bg-white transition-colors duration-300 ${schedule.completed ? 'text-indigo-500' : 'text-slate-300'}`}>
                                    {schedule.completed ? <CheckCircle2 className="w-[20px] h-[20px] fill-indigo-100" /> : <Circle className="w-[20px] h-[20px]" />}
                                </div>

                                {/* Content */}
                                <div className="flex-1 cursor-pointer select-none" onClick={() => setExpandedId(expandedId === schedule.id ? null : schedule.id)}>
                                    <div className="flex flex-wrap items-center gap-2">
                                        <span className="text-[10px] font-bold bg-slate-100 px-1.5 py-0.5 rounded text-slate-500">{schedule.category}</span>
                                        <p className={`text-base font-bold transition-all ${schedule.completed ? 'text-slate-400 line-through' : 'text-slate-800'}`}>
                                            {schedule.title}
                                        </p>
                                        <span className="text-[10px] font-bold text-slate-400 border border-slate-200 px-1.5 rounded">{schedule.date}</span>
                                        <ChevronDown className={`w-4 h-4 text-slate-400 transition-transform duration-300 ${expandedId === schedule.id ? 'rotate-180' : ''}`} />
                                    </div>
                                    <p className={`text-[13px] mt-0.5 font-semibold ${schedule.completed ? 'text-slate-400' : 'text-indigo-500'}`}>
                                        {schedule.time}
                                    </p>
                                </div>

                                <button onClick={(e) => toggleSchedule(e, schedule.id)} className={`ml-4 w-8 h-8 shrink-0 rounded-[0.6rem] flex items-center justify-center transition-all shadow-sm border ${schedule.completed ? 'bg-indigo-50 text-indigo-600 border-indigo-200' : 'bg-white text-slate-300 border-slate-200 hover:border-indigo-300 hover:text-indigo-500'}`}>
                                    <Check className="w-4 h-4 stroke-[3]" />
                                </button>
                            </div>

                            {/* Expanded Detail */}
                            <div className={`overflow-hidden transition-all duration-300 ease-in-out pl-0 ${expandedId === schedule.id ? 'max-h-40 opacity-100 mt-3' : 'max-h-0 opacity-0'}`}>
                                <div className="text-sm text-slate-600 bg-slate-50 p-4 rounded-xl border border-slate-200/60 leading-relaxed shadow-inner">
                                    {schedule.memo || '?묒꽦???곸꽭 硫붾え媛 ?놁뒿?덈떎.'}
                                    <div className="mt-4 flex gap-2">
                                        <button onClick={() => deleteSchedule(schedule.id)} className="text-[11px] font-bold flex items-center gap-1 text-rose-500 bg-rose-50 px-3 py-1.5 rounded-lg hover:bg-rose-100 transition-colors">
                                            <Trash2 className="w-3 h-3" /> ??젣
                                        </button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}
